mapper.select:
select a.id, a.table_id, a.column_name, a.column_type, a.column_length, a.data_type, a.null_able, a.plugin, a.properties_name, a.properties_type, a.comments, a.gen_flag, a.hide_flag, a.sort_flag, a.sequence, a.create_user, a.create_date, a.update_user, a.update_date, a.column_key, b.id AS columnElementsId, b.column_id, b.element_id, b.element_value, b.create_user, b.create_date, b.update_user, b.update_date, c.description, c.element_name, c.required, c.default_val from gen_columns a left join gen_column_element b on a.id=b.column_id left join gen_element c on c.id=b.element_id
a.id=#{id}
AND a.table_id=#{tableId}
AND a.column_name=#{columnName}
AND a.data_type=#{dataType}
AND a.plugin=#{plugin}
AND a.properties_name=#{propertiesName}
AND a.properties_type=#{propertiesType}
AND a.comments=#{comments}
AND a.gen_flag=#{genFlag}
AND a.hide_flag=#{hideFlag}
AND a.sort_flag=#{sortFlag}
AND a.sequence=#{sequence}
AND a.create_user=#{createUser}
AND a.create_date=#{createDate}
AND a.update_user=#{updateUser}
AND a.update_date=#{updateDate}
order by a.sequence,c.sequence

select 

from gen_columns


			id=#{id}


			AND table_id=#{tableId}


			AND column_name=#{columnName}


			AND column_key=#{columnKey}


			AND data_type=#{dataType}


			AND plugin=#{plugin}


			AND properties_name=#{propertiesName}


			AND properties_type=#{propertiesType}


			AND comments=#{comments}


			AND gen_flag=#{genFlag}


			AND hide_flag=#{hideFlag}


			AND sort_flag=#{sortFlag}


			AND sequence=#{sequence}


			AND create_user=#{createUser}


			AND create_date=#{createDate}


			AND update_user=#{updateUser}


			AND update_date=#{updateDate}

mapper.delete:
delete from gen_columns 
WHERE id IN 

#{idList}
