FROM tb_member m LEFT JOIN tb_member_info mi ON m.id = mi.member_id
m.id=#{id}

		SELECT 

		FROM tb_member m
        LEFT JOIN tb_member_info mi ON m.id = mi.member_id


					AND m.id=#{id}


