mapper.select:
select 

from tb_single single


			AND single.id=#{id}


			AND single.test_one=#{testOne}


			AND single.test_two=#{testTwo}


			AND single.test_three=#{testThree}


			AND single.create_user=#{createUser}


			AND single.update_user=#{updateUser}


			AND single.create_date=#{createDate}


			AND single.update_date=#{updateDate}

mapper.delete:
delete from tb_single 
WHERE id IN 

#{idList}
